const { GraphQLObjectType, GraphQLID, GraphQLString, GraphQLList } = require('graphql')
const Profile = require('../models/Profile')

const ProfileType = new GraphQLObjectType({
  name: 'Profile',
  fields: () => ({
    id: {type: GraphQLID },
    email: {type: GraphQLString }
  })
})

module.exports = {
  profile: ProfileType
}
