const vertex = require('vertex360')({site_id: process.env.TURBO_APP_ID})
const Controller = vertex.Controller
const Base = require('../models/Base')

class BaseController extends Controller {
	constructor(){
		super(Base, process.env)
	}

	get(params) {
		return new Promise((resolve, reject) => {
			Controller.checkCollectionDB(Base.collectionName())
			.then(data => {
				return Base.find(params, Controller.parseFilters(params))
			})
			.then(bases => {
				resolve(Base.convertToJson(bases))
			})
			.catch(err => {
				reject(err)
			})
		})
	}

	getById(id) {
		return new Promise((resolve, reject) => {
			Controller.checkCollectionDB(Base.collectionName())
			.then(data => {
				return Base.findById(id)
			})
			.then(base => {
				if (base == null){
					throw new Error(Base.resourceName + ' ' + id + ' not found.')
					return
				}

				resolve(base.summary())
			})
			.catch(err => {
				reject(new Error(Base.resourceName + ' ' + id + ' not found.'))
			})
		})
	}

	post(body) {
		return new Promise((resolve, reject) => {
			let payload = null
			Base.create(body)
			.then(base => {
				payload = base.summary()
				return (process.env.TURBO_ENV=='dev') ? null : Controller.syncCollection(Base.collectionName())
			})
			.then(data => {
				resolve(payload)
			})
			.catch(err => {
				reject(err)
			})
		})
	}

	put(id, params) {
		return new Promise((resolve, reject) => {
			let payload = null
			Base.findByIdAndUpdate(id, params, {new:true})
			.then(base => {
				payload = base.summary()
				return (process.env.TURBO_ENV=='dev') ? null : Controller.syncCollection(Base.collectionName())
			})
			.then(data => {
				resolve(payload)
			})
			.catch(err => {
				reject(err)
			})
		})
	}

	delete(id) {
		return new Promise((resolve, reject) => {
			Base.findByIdAndRemove(id)
			.then(() => {
				return (process.env.TURBO_ENV=='dev') ? null : Controller.syncCollection(Base.collectionName())
			})
			.then(data => {
				resolve()
			})
			.catch(err => {
				reject(err)
			})
		})
	}
}

module.exports = BaseController
