const vertex = require('vertex360')({site_id: process.env.TURBO_APP_ID})
const Controller = vertex.Controller
const Profile = require('../models/Profile')

class ProfileController extends Controller {
	constructor(){
		super(Profile, process.env)
	}

	get(params) {
		return new Promise((resolve, reject) => {
			Controller.checkCollectionDB(Profile.collectionName())
			.then(data => {
				return Profile.find(params, Controller.parseFilters(params))
			})
			.then(profiles => {
				resolve(Profile.convertToJson(profiles))
			})
			.catch(err => {
				reject(err)
			})
		})
	}

	getById(id) {
		return new Promise((resolve, reject) => {
			Controller.checkCollectionDB(Profile.collectionName())
			.then(data => {
				return Profile.findById(id)
			})
			.then(profile => {
				if (profile == null){
					throw new Error(Profile.resourceName + ' ' + id + ' not found.')
					return
				}

				resolve(profile.summary())
			})
			.catch(err => {
				reject(new Error(Profile.resourceName + ' ' + id + ' not found.'))
			})
		})
	}

	post(body) {
		return new Promise((resolve, reject) => {
			let payload = null
			Profile.create(body)
			.then(profile => {
				payload = profile.summary()
				return (process.env.TURBO_ENV=='dev') ? null : Controller.syncCollection(Profile.collectionName())
			})
			.then(data => {
				resolve(payload)
			})
			.catch(err => {
				reject(err)
			})
		})
	}

	put(id, params) {
		return new Promise((resolve, reject) => {
			let payload = null
			Profile.findByIdAndUpdate(id, params, {new:true})
			.then(profile => {
				payload = profile.summary()
				return (process.env.TURBO_ENV=='dev') ? null : Controller.syncCollection(Profile.collectionName())
			})
			.then(data => {
				resolve(payload)
			})
			.catch(err => {
				reject(err)
			})
		})
	}

	delete(id) {
		return new Promise((resolve, reject) => {
			Profile.findByIdAndRemove(id)
			.then(() => {
				return (process.env.TURBO_ENV=='dev') ? null : Controller.syncCollection(Profile.collectionName())
			})
			.then(data => {
				resolve()
			})
			.catch(err => {
				reject(err)
			})
		})
	}
}

module.exports = ProfileController
